define('stats/tweet-limit',['require','jquery','twitter-text'],function(require) {
    "use strict";

    var $ = require('jquery');
    var twitterText = require('twitter-text');

    var TWEET_LENGTH = 140;

    var TweetLimit = function($el) {
        this.$el = $el;
    };

    TweetLimit.prototype.start = function() {
        this.template = this.$el.data('tweet-limit-string');
        this.$counter = $('<p class="form-row-help-text"></p>');
        this.$el.after(this.$counter);
        this.updateCounter();
        this.$el.on('keyup.tweet-limit contextmenu.tweet-limit input.tweet-limit', this.updateCounter.bind(this));
    };

    TweetLimit.prototype.stop = function() {
        this.$el.off('.tweet-limit');
    };

    TweetLimit.prototype.updateCounter = function() {
        var text = this.$el.val();
        var remaining = TWEET_LENGTH - twitterText.getTweetLength(text);
        var count = remaining + '/' + TWEET_LENGTH;
        this.$counter.text(this.template.replace('{}', count));
        this.$counter.toggleClass('form-row-help-text--error', remaining < 0);
    };

    return TweetLimit;
});

define('stats/tweet-preview',['require','jquery','twitter-text'],function(require) {
    "use strict";

    var $ = require('jquery');
    var twitterText = require('twitter-text');

    var TweetPreview = function($el) {
        this.$el = $el;
        this.$previewArea = null;
        this.$previewButton = null;
        this.$editButton = null;
    };

    TweetPreview.prototype.start = function() {
        this.$editButton = this.createEditButton();
        this.$editButton.on('click', this.editMode.bind(this));
        this.$el.before(this.$editButton);

        this.$previewButton = this.createPreviewButton();
        this.$previewButton.on('click', this.previewMode.bind(this));
        this.$el.before(this.$previewButton);

        this.$previewArea = this.createPreviewArea();
    };

    TweetPreview.prototype.previewMode = function() {
        this.$editButton.prop('disabled', false);
        this.$previewButton.prop('disabled', true);
        this.$el.prop('readonly', true);

        this.showPreview();
    };

    TweetPreview.prototype.editMode = function() {
        this.$editButton.prop('disabled', true);
        this.$previewButton.prop('disabled', false);
        this.$el.prop('readonly', false);

        this.removePreview();
    };

    TweetPreview.prototype.showPreview = function() {
        var preview = twitterText.autoLink(this.$el.val(), { usernameIncludeSymbol: true });
        this.$previewArea.html(preview);
        this.$previewArea.height(this.$el.outerHeight());
        this.$el.after(this.$previewArea);
    };

    TweetPreview.prototype.removePreview = function() {
        this.$previewArea.detach();
    };

    TweetPreview.prototype.createEditButton = function() {
        var editText = this.$el.data('tweetPreviewEditText');
        var $editButton = this.createButton(editText);
        $editButton.prop('disabled', true);
        return $editButton;
    };

    TweetPreview.prototype.createPreviewButton = function() {
        var previewText = this.$el.data('tweetPreviewPreviewText');
        return this.createButton(previewText);
    };

    TweetPreview.prototype.createButton = function(text) {
        var $button = $('<button></button>');
        $button.addClass('tweet-preview-button');
        $button.text(text);
        return $button;
    };

    TweetPreview.prototype.createPreviewArea = function() {
        var $previewArea = $('<div></div>');
        $previewArea.addClass('tweet-preview');
        $previewArea.addClass('form-control');
        $previewArea.on('click', 'a', function(evt) {
            evt.preventDefault();
            window.open(this.href);
        });
        return $previewArea;
    };

    return TweetPreview;
});
