define('kerve/helpers/windowscroll',['jquery'], function($) {
	
	
	/****************************** DEFINE ******************************/
	var kscroll = {
		//DATA
		use: false,
		win_h: 0,
		scrolled_y: 0,
		visible_y: 0,
		//START & STOP
		start: function(){
			if(kscroll.use == false){
				kscroll.use = true;
				$(window).on('scroll',kscroll.scroll_pos);
			};
		},
		stop: function(){
			if(kscroll.use == true){
				kscroll.use = false;
				$(window).off('scroll',kscroll.scroll_pos);
			};
		},
		reset: function(){
			kscroll.stop();
			kscroll.functions = {};
		},
		//CALCULATE POSITION
		scroll_pos: function(){
			if(kscroll.use==true){
				kscroll.win_h = $(window).height();
				kscroll.scrolled_y = $(window).scrollTop();
				kscroll.visible_y = kscroll.scrolled_y + kscroll.win_h;
				//run functions
				for(var i in kscroll.functions){
					//scroll init
					if(kscroll.functions[i].done==false && kscroll.visible_y>=kscroll.functions[i].pos){
						kscroll.functions[i].func();
						kscroll.functions[i].done = true;
					};
					//pause
					if(kscroll.functions[i].paused==false && typeof(kscroll.functions[i].pause_func)!=='undefined' && (kscroll.visible_y<kscroll.functions[i].pause_top || kscroll.scrolled_y>kscroll.functions[i].pause_bottom)){
						kscroll.functions[i].paused = true;
						kscroll.functions[i].pause_func();
					};
					//resume
					if(kscroll.functions[i].paused==true && typeof(kscroll.functions[i].resume_func)!=='undefined' && kscroll.visible_y>=kscroll.functions[i].pause_top && kscroll.scrolled_y<=kscroll.functions[i].pause_bottom){
						kscroll.functions[i].paused = false;
						kscroll.functions[i].resume_func();
					};
				};
			};
		},
		//FUNCTIONS
		functions: {},
		add: function(name,pos,func,pause_top,pause_bottom,pause_func,resume_func){
			if(typeof(kscroll.functions[name])==='undefined'){
				kscroll.functions[name] = {
					name: name,
					//scroll init
					pos: pos,
					func: func,
					//pause & resume
					pause_top: pause_top,
					pause_bottom: pause_bottom,
					pause_func: pause_func,
					resume_func: resume_func,
					done: false,
					paused: false
				};
				kscroll.start();
				kscroll.scroll_pos();
			};
		},
		update_pos: function(name,pos,pause_top,pause_bottom){
			if(typeof(kscroll.functions[name])!=='undefined'){
				kscroll.functions[name].pos = pos;
				kscroll.functions[name].pause_top = pause_top;
				kscroll.functions[name].pause_bottom = pause_bottom;
			};
		},
		remove: function(name){
			if(typeof(kscroll.functions[name])!=='undefined'){
				delete kscroll.functions[name];
			};
		},
		done: function(name){
			if(typeof(kscroll.functions[name])!=='undefined'){
				return kscroll.functions[name].done;
			}else{
				return false;
			};
		}
	};
	
	
	/****************************** RETURN ******************************/
	return {
		//START & STOP
		start: function(){
			kscroll.start();
		},
		stop: function(){
			kscroll.stop();
		},
		reset: function(){
			kscroll.reset();
		},
		//DATA
		scrolled_y: function(){
			return kscroll.scrolled_y
		},
		//FUNCTIONS
		add: function(name,pos,func,pause_top,pause_bottom,pause_func,resume_func){
			kscroll.add(name,pos,func,pause_top,pause_bottom,pause_func,resume_func);
		},
		update_pos: function(name,pos,pause_top,pause_bottom){
			kscroll.update_pos(name,pos,pause_top,pause_bottom);
		},
		remove: function(name){
			kscroll.remove(name);
		},
		done: function(name){
			return kscroll.done(name);
		}
	};
	
	
});

