define('kerve/helpers/windowsize',['jquery'], function($) {
	
	
	/****************************** DEFINE ******************************/
	var windowsize = {
		//CONFIG
		freq: 100,
		//DATA
		use: false,
		timeout: null,
		w: 0,
		h: 0,
		r: 0,
		//START & STOP
		start: function(){
			if(windowsize.use == false){
				windowsize.use = true;
				$(window).on('resize',windowsize.run);
			};
		},
		stop: function(){
			if(windowsize.use == true){
				windowsize.use = false;
				$(window).off('resize',windowsize.run);
			};
		},
		reset: function(){
			windowsize.stop();
			windowsize.functions = {};
		},
		//RUN
		run: function(force){
			if(windowsize.use == true){
				if(force==true){
					windowsize.func(force);
				}else{
					clearTimeout(windowsize.timeout);
					windowsize.timeout = setTimeout(function(){
						windowsize.func(force);
					}, windowsize.freq);
				};
			};
		},
		func: function(force){
			console.log('%c--- windowsize ---','color:#ddc000');
			var oldWidth = windowsize.w;
			var oldHeight = windowsize.h;
			windowsize.w = $(window).width();
			windowsize.h = window.innerHeight || $(window).height();
			windowsize.r = windowsize.w/windowsize.h;
			if(force == true){
				windowsize.w_func();
				windowsize.h_func();
				windowsize.x_func();
			}else{
				if(windowsize.w!=oldWidth){
					windowsize.w_func();
				};
				if(windowsize.h!=oldHeight){
					windowsize.h_func();
				};
				if(windowsize.w!=oldWidth || windowsize.h!=oldHeight){
					windowsize.x_func();
				};
			};
		},
		//CALLBACK FUNCTIONS
		w_func: function(){
			for(var i in windowsize.functions){
				if(typeof(windowsize.functions[i].w_func)!=='undefined'){
					windowsize.functions[i].w_func();
				};
			};
		},
		h_func: function(){
			for(var i in windowsize.functions){
				if(typeof(windowsize.functions[i].h_func)!=='undefined'){
					windowsize.functions[i].h_func();
				};
			};
		},
		x_func: function(){
			for(var i in windowsize.functions){
				if(typeof(windowsize.functions[i].x_func)!=='undefined'){
					windowsize.functions[i].x_func();
				};
			};
		},
		//FUNCTIONS
		functions: {},
		add: function(name,data){
			windowsize.functions[name] = data;
			windowsize.start();
		},
		remove: function(){
			if(typeof(windowsize.functions[name])!=='undefined'){
				delete windowsize.functions[name];
			};
		}
	};
	
	
	/****************************** RETURN ******************************/
	return {
		//START & STOP
		start: function(){
			windowsize.start();
		},
		stop: function(){
			windowsize.stop();
		},
		reset: function(){
			windowsize.reset();
		},
		//DATA
		w: function(){
			return windowsize.w;
		},
		h: function(){
			return windowsize.h;
		},
		r: function(){
			return windowsize.r;
		},
		//FUNCTIONS
		add: function(name,data){
			windowsize.add(name,data);
		},
		remove: function(name){
			windowsize.remove(name);
		}
	};
	
	
});

