define(
	'kerve/widgets/2-2-listeningnow',['jquery','core/ajax','kerve/helpers/windowscroll','kerve/helpers/windowsize','kerve/helpers/helper','kerve/plugins/jquery.touchSwipe.1.6.min'],
	function($,ajax,windowscroll,windowsize,helper,touchswipe){
	
	
	/****************************** DEFINE ******************************/
	var w = function($el,app){
		this.el = $el;
	};
	
	
	/****************************** START ******************************/
	w.prototype.start = function() {
		//CONSOLE LOGS
		m.logs = (helper.config.logs==true || m.dev==true?true:false);
		if(m.logs) console.log('%c*** WIDGET - 2.2 USERS LISTENING NOW - START ***','color:#098c0c;');
		//CACHE ELEMENTS
		m.el = this.el;
		m.query_user = m.el.attr('data-user') || '';
		m.query_artist = m.el.attr('data-artist') || '';
		m.query_album = m.el.attr('data-album') || '';
		m.query_track = m.el.attr('data-track') || '';
		m.locale = m.el.attr('data-locale') || '';
		m.locale_plays = m.el.attr('data-locale-plays') || '';
		cache();
		//NAV
		nav();
		//INIT
		get_data();
	};
	
	
	/****************************** STOP ******************************/
	w.prototype.stop = function() {
		if(m.logs) console.log('%c*** WIDGET - 2.2 USERS LISTENING NOW - STOP ***','color:#098c0c;');
		//CLEAR INTERVALS & TIMEOUTS
		clearInterval(m.int_data);
		clearInterval(m.int_animate);
		clearTimeout(m.timeout_pause);
		//CLEAR DATA
		data = [];
		data2 = [];
	};
	
	
	/****************************** MODELS ******************************/
	var m = {
		//CONFIG
		dev: false, //dev mode?
		query_url: 'https://kerve.last.fm/kerve/listeningnow',
		data_quantity: 6, //how many results to fetch
		data_frequency: 15000, //time between data queries
		pause_timeout: 2000, //time to pause after interaction
		slide_time: 500, //time to slide between frames
		//DATA
		total: 0, //stores length of data array
		current: 0, //stores current slide
		got_data: false, //stores whether data has been fetched
		//INTERVALS
		int_data: null, //get_data interval
		int_animate: null, //animation interval
		timeout_pause: null //pause timeout
	};
	var data = []; //stores users currently on page
	var data2 = []; //stores new data from ajax
	
	
	/****************************** CACHE ELEMENTS ******************************/
	function cache(){
		m.slider = m.el.find('.nowslider');
	};
	
	
	/****************************** NAV ******************************/
	function nav(){
		//HOVER
		m.el.on('mouseenter','.nowwrap',function(){
			pause();
		});
		m.el.on('mouseleave','.nowwrap',function(){
			resume();
		});
		//ARROWS
		m.el.find('.nowleft').on('click',function(){
			slide_left();
			return false;
		});
		m.el.find('.nowright').on('click',function(){
			slide_right();
			return false;
		});
		//SWIPE
		m.slider.swipe({
			swipeRight: function(){
				slide_left();
			},
			swipeLeft: function(){
				slide_right();
			},
			threshold:100,
			excludedElements: ''
		});
	};
	
	
	/****************************** GET DATA ******************************/
	//INIT
	function get_data(){
		get_data_ajax(get_data_callback1);
	};
	//AJAX
	function get_data_ajax(callback){
		ajax.get(m.query_url, null, { //url, options, data, callback
			artist:helper.query_format(m.query_artist),
			album:helper.query_format(m.query_album),
			track:helper.query_format(m.query_track),
			user:helper.query_format(m.query_user),
			limit: 6
		}, function(a,b,c,d){
			if(m.logs) console.log('--- ajax response - 2.2 listening now ---');
			if(m.logs) console.log(b);
			if(typeof(b.Users)!=='undefined'){
				for(var i in b.Users){
					data2.push(b.Users[i]);
				};
				if(typeof(callback)==='function'){
					callback();
				};
			};
		});
	};
	//CALLBACKS
	function get_data_callback1(){
		for(i=0;i<(m.data_quantity-1);i++){
			animate2(true);
		};
		animate2();
		m.got_data = true;
		//continue
		get_data_ajax(get_data_callback2);
	};
	function get_data_callback2(){
		//animate
		animate();
		//preload images
		for(var i in data){
			if(data[i].UserImage!=''){
				var img = new Image();
				img.src = data[i].UserImage;
			};
		};
		//loop ajax
		m.int_data = setInterval(function(){
			get_data_ajax();
		},m.data_frequency);
	};
	
	
	/****************************** ANIMATE ******************************/
	//SCROLL INIT
	function scroll_init(){
		
	};
	function animate(){
		m.int_animate = setInterval(function(){
			animate2();
		},(m.data_frequency/(m.data_quantity-1)));
	};
	function animate2(force){
		if(data2.length>0){
			//ARRAY
			data.push(data2[0])
			data2.shift();
			m.total = data.length;
			if(m.total>6){
				data.shift();
			};
			//OUTPUT
			var user = data[data.length-1].UserName;
			var user_url = data[data.length-1].user_url;
			if(user.length>17){
				user = user.substr(0, 16)+'...';
			};
			var artist = data[data.length-1].Track.Artist;
			var artist_url = data[data.length-1].artist_url;
			if(artist.length>22){
				artist = artist.substr(0, 21)+'...';
			};
			var track = data[data.length-1].Track.Name;
			var track_url = data[data.length-1].track_url;
			if(track.length>22){
				track = track.substr(0, 21)+'...';
			};
			var scrobbles = data[data.length-1].Track.UserScrobbles;
			var img = data[data.length-1].UserImage;
			m.slider.append('<div class="slide" style="left:100%;">\
				<div class="slide_inner">\
					<div class="nowslider_imgwrap"><div class="nowslider_img" '+(img!=''?'style="background-image:url('+img+');"':'')+'></div></div>\
					<div class="nowslider_copywrap">\
						<a class="nowslider_name" href="'+helper.url(user_url,m.locale)+'">'+user+'</a><br />\
						<a class="nowslider_artist" href="'+helper.url(artist_url,m.locale)+'">'+artist+'</a><br />\
						<a class="nowslider_track" href="'+helper.url(track_url,m.locale)+'">'+track+'</a>\
						<div class="nowslider_scrobbles">'+m.locale_plays+': '+helper.commas(scrobbles)+'</div>\
					</div>\
				</div>\
			</div>');
			//REMOVE
			if(m.total>m.data_quantity){
				m.el.find('.nowslider .slide:first-child').remove();
			};
			//ANIMATE
			if(force!=true){
				slideto(m.data_quantity);
			};
		};
	};
	function slideto(id,dir){
		m.current = id;
		m.el.find('.nowslider .slide.current').stop().removeClass('current').css({
			left:0,
			opacity: 1
		}).animate({
			left:(dir=='left'?'':'-')+'100%',
			opacity: 0
		},m.slide_time);
		m.el.find('.nowslider .slide:nth-child('+id+')').stop().addClass('current').css({
			left:(dir=='left'?'-':'')+'100%',
			opacity: 0
		}).animate({
			left:0,
			opacity: 1
		},m.slide_time);
	};
	function slide_left(){
		if(m.current>2){
			var next = m.current-1;
		}else{
			var next = m.data_quantity;
		};
		pause();
		slideto(next,'left');
	};
	function slide_right(){
		if(m.current<m.data_quantity){
			var next = m.current+1;
		}else{
			var next = 1;
		};
		pause();
		slideto(next);
	};
	function pause(xresume){
		clearInterval(m.int_animate);
		clearTimeout(m.timeout_pause);
		if(xresume==true){
			resume();
		};
	};
	function resume(){
		clearInterval(m.int_animate);
		clearTimeout(m.timeout_pause);
		m.timeout_pause = setTimeout(function(){
			animate();
		},m.pause_timeout);
	};
	
	
	/****************************** RETURN ******************************/
	return w;
	
	
});

